import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from '../context/AppContext';

const Budget = () => {
  const { budget, expenses, setBudget } = useContext(AppContext);  // Include setBudget from context
  const [newBudget, setNewBudget] = useState(budget);

  // Calculate total expenses
  const totalExpenses = expenses.reduce((total, item) => {
    return (total += item.cost);
  }, 0);

  // Calculate remaining budget
  const remaining = budget - totalExpenses;

  // Function to handle budget change
  const handleBudgetChange = (event) => {
    const value = parseInt(event.target.value, 10);
    if (!isNaN(value) && value <= remaining) {
      setNewBudget(value);
    }
  };

  // Function to update global budget
  const handleUpdateBudget = () => {
    setBudget(newBudget);
  };

  // Functions to increase and decrease budget by 10
  const increaseBudget = () => {
    const increasedBudget = newBudget + 10;
    setNewBudget(increasedBudget);
    setBudget(increasedBudget);
  };

  const decreaseBudget = () => {
    const decreasedBudget = newBudget - 10;
    if (decreasedBudget >= 0 && decreasedBudget <= remaining) {
      setNewBudget(decreasedBudget);
      setBudget(decreasedBudget);
    }
  };

  // Effect to update displayed budget when the context changes
  useEffect(() => {
    setNewBudget(budget);
  }, [budget]);

  return (
    <div className='alert alert-secondary'>
      <span>Budget: £{budget}</span>
      <input
        type="number"
        min="0"
        max={remaining}
        step="10"
        value={newBudget}
        onChange={handleBudgetChange}
      />
      <button onClick={handleUpdateBudget}>Update Budget</button>
      <button onClick={increaseBudget}>Increase by £10</button>
      <button onClick={decreaseBudget}>Decrease by £10</button>
    </div>
  );
};

export default Budget;
