import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from '../context/AppContext';

const Budget = () => {
  const { budget, expenses, setBudget } = useContext(AppContext);
  const [newBudget, setNewBudget] = useState(budget);
  const [errorMessage, setErrorMessage] = useState('');

  // Calculate total expenses
  const totalExpenses = expenses.reduce((total, item) => total + item.cost, 0);

  // Validate the new budget value
  const validateBudget = (value) => {
    if (value > 20000) {
      setErrorMessage('Budget cannot be more than £20,000.');
      return false;
    } else if (value < totalExpenses) {
      setErrorMessage('Budget cannot be less than current spending.');
      return false;
    } else {
      setErrorMessage('');
      return true;
    }
  };

  // Function to handle budget change
  const handleBudgetChange = (event) => {
    const value = parseInt(event.target.value, 10);
    if (!isNaN(value)) {
      setNewBudget(value);
      validateBudget(value);
    }
  };

  // Function to update global budget
  const handleUpdateBudget = () => {
    if (validateBudget(newBudget)) {
      setBudget(newBudget);
    }
  };

  // Functions to increase and decrease budget by 10
  const changeBudgetBy = (amount) => {
    const updatedBudget = newBudget + amount;
    if (validateBudget(updatedBudget)) {
      setNewBudget(updatedBudget);
      setBudget(updatedBudget);
    }
  };

  // Effect to update displayed budget when the context changes
  useEffect(() => {
    setNewBudget(budget);
  }, [budget]);

  return (
    <div className='alert alert-secondary'>
      <span>Budget: £{budget}</span>
      <input
        type="number"
        min={totalExpenses}
        max="20000"
        step="10"
        value={newBudget}
        onChange={handleBudgetChange}
      />
      <button onClick={handleUpdateBudget}>Update Budget</button>
      <button onClick={() => changeBudgetBy(10)}>Increase by £10</button>
      <button onClick={() => changeBudgetBy(-10)}>Decrease by £10</button>
      {errorMessage && <div className="alert alert-danger mt-2">{errorMessage}</div>}
    </div>
  );
};

export default Budget;
