import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from '../context/AppContext';

const Budget = () => {
  const { budget, expenses, setBudget } = useContext(AppContext);
  const [newBudget, setNewBudget] = useState(budget.toString()); // convert to string
  const [errorMessage, setErrorMessage] = useState('');

  // Calculate total expenses
  const totalExpenses = expenses.reduce((total, item) => total + item.cost, 0);

  // Function to handle budget change
  const handleBudgetChange = (event) => {
    const value = event.target.value; // keep it as string

    setNewBudget(value);

    const intValue = parseInt(value, 10);

    if (isNaN(intValue)) {
      setErrorMessage('Please enter a valid number.');
      return;
    }

    if (intValue > 20000) {
      setErrorMessage('Budget cannot be more than £20,000.');
      return;
    }

    if (intValue < totalExpenses) {
      setErrorMessage('Budget cannot be less than current spending.');
    } else {
      setErrorMessage('');
    }
  };

  // Function to update global budget
  const handleUpdateBudget = () => {
    const intValue = parseInt(newBudget, 10);

    if (!isNaN(intValue) && intValue >= totalExpenses && intValue <= 20000) {
      setBudget(intValue);
      setErrorMessage('');
    }
  };

  // Functions to increase and decrease budget by 10
  const increaseBudget = () => {
    const increasedBudget = parseInt(newBudget, 10) + 10;
    if (increasedBudget <= 20000) {
      setNewBudget(increasedBudget.toString()); // convert to string
      setBudget(increasedBudget);
    }
  };

  const decreaseBudget = () => {
    const decreasedBudget = parseInt(newBudget, 10) - 10;
    if (decreasedBudget >= totalExpenses) {
      setNewBudget(decreasedBudget.toString()); // convert to string
      setBudget(decreasedBudget);
    }
  };

  // Effect to update displayed budget when the context changes
  useEffect(() => {
    setNewBudget(budget.toString()); // convert to string
  }, [budget]);

  return (
    <div className='alert alert-secondary'>
      <span>Budget: £{budget}</span>
      <input
        type="text" // change type to text
        maxLength="5"
        value={newBudget}
        onChange={handleBudgetChange}
      />
      <button onClick={handleUpdateBudget}>Update Budget</button>
      <button onClick={increaseBudget}>Increase by £10</button>
      <button onClick={decreaseBudget}>Decrease by £10</button>
      {errorMessage && <div className="alert alert-danger mt-2">{errorMessage}</div>}
    </div>
  );
};

export default Budget;
